import React from "react";
import { Link } from "react-router-dom";
import './Profile.css';
import { defaultImgs } from "../defaultimgs";
import TweetInFeed from "../components/TweetInFeed";
import { useMoralis } from "react-moralis";


const Profile = () => {
  const { Moralis } = useMoralis();
  const user = Moralis.User.current();


  return (
    <>
    <div className="pageIdentify">Profile</div>
    <img alt="" className="profileBanner" src={user.attributes.banner ? user.attributes.banner : defaultImgs[1]}></img>
    <div className="pfpContainer">
      <img alt="" className="profilePFP" src={user.attributes.pfp ? user.attributes.pfp : defaultImgs[0]}></img>
      <div className="profileName">DollarBull</div>
      <div className="profileWallet">0x42..314</div>
      <Link to="/settings">
        <div className="profileEdit">Edit Profile</div>
      </Link>
      <div className="profileBio">
        Your Average Web3 Mage
      </div>
      <div className="profileTabs">
        <div className="profileTab">
          Your Tweet
        </div>
      </div>
    </div>
    <TweetInFeed profile={true}></TweetInFeed>
    </>
  );
};

export default Profile;
